import React, { useState } from 'react';

function Settings({ operations, onClearData }) {
  const [showConfirmDialog, setShowConfirmDialog] = useState(false);

  const handleClearData = () => {
    onClearData();
    setShowConfirmDialog(false);
  };

  return (
    <div className="settings-page">
      <h1>Настройки</h1>

      <div className="settings-section">
        <h2>Статистика</h2>
        <div className="stats-info">
          <p>Всего транзакций: {operations.length}</p>
          <p>Доходов: {operations.filter(op => op.type === 'income').length}</p>
          <p>Расходов: {operations.filter(op => op.type === 'expense').length}</p>
        </div>
      </div>

      <div className="settings-section">
        <h2>Управление данными</h2>
        <div className="danger-zone">
          <h3>Опасная зона</h3>
          <p>Удаление всех данных приведет к безвозвратной потере всей информации о транзакциях.</p>
          <button 
            className="danger-button"
            onClick={() => setShowConfirmDialog(true)}
          >
            Удалить все данные
          </button>
        </div>
      </div>

      {showConfirmDialog && (
        <div className="confirm-dialog">
          <div className="confirm-content">
            <h3>Подтверждение удаления</h3>
            <p>Вы уверены, что хотите удалить все данные? Это действие нельзя отменить.</p>
            <div className="confirm-actions">
              <button 
                className="confirm-button"
                onClick={handleClearData}
              >
                Удалить
              </button>
              <button 
                className="cancel-button"
                onClick={() => setShowConfirmDialog(false)}
              >
                Отмена
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Settings;
