import React from 'react';
import { Link } from 'react-router-dom';

function Dashboard({ operations }) {
  const getTotalAmount = (type) => {
    return operations
      .filter(op => op.type === type)
      .reduce((sum, op) => sum + op.amount, 0);
  };

  const recentOperations = operations.slice(-5).reverse();

  return (
    <div className="dashboard">
      <h1>Панель управления</h1>
      
      <div className="dashboard-summary">
        <div className="summary-card income">
          <h3>Общий доход</h3>
          <p className="amount">{getTotalAmount('income')} ₽</p>
        </div>
        <div className="summary-card expense">
          <h3>Общий расход</h3>
          <p className="amount">{getTotalAmount('expense')} ₽</p>
        </div>
        <div className="summary-card balance">
          <h3>Баланс</h3>
          <p className="amount">{getTotalAmount('income') - getTotalAmount('expense')} ₽</p>
        </div>
      </div>

      <div className="quick-actions">
        <Link to="/add" className="action-button">
          Добавить транзакцию
        </Link>
        <Link to="/transactions" className="action-button">
          Все транзакции
        </Link>
      </div>

      <div className="recent-transactions">
        <h2>Последние транзакции</h2>
        {recentOperations.length === 0 ? (
          <p className="no-data">Нет транзакций</p>
        ) : (
          <div className="transactions-list">
            {recentOperations.map(operation => (
              <div key={operation.id} className={`transaction-item ${operation.type}`}>
                <div className="transaction-date">
                  {new Date(operation.date).toLocaleDateString('ru-RU')}
                </div>
                <div className="transaction-info">
                  <span className="transaction-type">
                    {operation.type === 'income' ? 'Доход' : 'Расход'}
                  </span>
                  {operation.category && (
                    <span className="transaction-category">{operation.category}</span>
                  )}
                </div>
                <div className="transaction-amount">
                  {operation.type === 'income' ? '+' : '-'}{operation.amount} ₽
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default Dashboard;
