import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [isListening, setIsListening] = useState(false);
  const [recognizedText, setRecognizedText] = useState('');
  const [recognition, setRecognition] = useState(null);

  useEffect(() => {
    // Проверяем поддержку Web Speech API
    if (!('webkitSpeechRecognition' in window) && !('SpeechRecognition' in window)) {
      alert('Ваш браузер не поддерживает распознавание речи. Попробуйте использовать Chrome.');
      return;
    }

    // Инициализируем распознавание речи
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    const recognitionInstance = new SpeechRecognition();
    
    recognitionInstance.continuous = true;
    recognitionInstance.interimResults = true;
    recognitionInstance.lang = 'ru-RU';

    recognitionInstance.onresult = (event) => {
      const transcript = Array.from(event.results)
        .map(result => result[0])
        .map(result => result.transcript)
        .join('');
      
      setRecognizedText(transcript);
    };

    recognitionInstance.onerror = (event) => {
      console.error('Ошибка распознавания речи:', event.error);
      setIsListening(false);
    };

    recognitionInstance.onend = () => {
      setIsListening(false);
    };

    setRecognition(recognitionInstance);

    return () => {
      if (recognitionInstance) {
        recognitionInstance.stop();
      }
    };
  }, []);

  const toggleListening = () => {
    if (isListening) {
      recognition.stop();
      setIsListening(false);
    } else {
      recognition.start();
      setIsListening(true);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Finance Voice Tracker</h1>
        <button 
          className="record-button"
          onClick={toggleListening}
        >
          {isListening ? 'Остановить запись' : 'Записать голос'}
        </button>
        <div className="transcript-container">
          <h2>Распознанный текст:</h2>
          <p className="transcript-text">{recognizedText}</p>
        </div>
      </header>
    </div>
  );
}

export default App;
